package drawing;

import javafx.scene.paint.Color;

public class Point {
	
	private double x;
	private double y;
	
	private Color color;

	public Point() {
		// TODO Auto-generated constructor st
		

	}

	public double getX() {
		return x;
	}

	public void setX(double x) {
		this.x = x;
	}

	public double getY() {
		return y;
	}

	public void setY(double y) {
		this.y = y;
	}

	public Color getColor() {
		return color;
	}

	public void setColor(Color color) {
		this.color = color;
	}

}
